import React, { Component } from 'react';

class ToDoList extends Component {

    constructor(){
        super()
        this.state ={
            myList : [],
            newvalue : ""

          }
    }

    // shouldComponentUpdate (){
      
    // }

    componentDidMount(){
        this.setState({myList:['Shoping', 'Reading'] })
        console.log(this.state.myList); 
    }

    handelOnClick(){
        this.setState({myList: [...this.state.myList, this.state.newvalue]})
      
    }
      


    render() {
      
        return (
<div>
    <h1> my list</h1>
<ul>
            {this.state.myList.map(function(name, index){
                return <li key={ index }>{name}    </li>;
              })}
        </ul>


        <div class="form-group">
      <label for="usr">Task:</label>
      {/* <input type="text" class="form-control" id="" onChange={(e) =>this.state.myList.concat(e.target.value)}  /> */}
      <input type="text" class="form-control" id="" onChange={(e) =>this.setState({ newvalue: e.target.value })}  />

      <button type="button" class="btn btn-primary btn-lg btn-block" >submit</button>

    </div>
       

</div>
        
        );
    }
}

export default ToDoList;
