import React, { Component } from 'react';

class ToDoList extends Component {

    constructor(){
        super()
        this.state ={
            myList : [],
            newvalue : "",
            currentValue: "",

          }
    }

    // shouldComponentUpdate (){
      
    // }

    componentDidMount(){
        this.setState({myList:['Shoping', 'Reading'] })
        console.log(this.state.myList); 
    }

     handelOnClick = (e) => {
        this.setState({myList: [...this.state.myList, this.state.newvalue]})
//this.forceUpdate()  = to rerender the componet

                       }
       
    // handelDelete = (e) => {
    //     let tempList = [...this.state.myList]
    //     tempList.pop(this.state.currentValue)
    //     this.setState( {myList: tempList} )
    // }
    handelDelete = (e) => {
        var array = [...this.state.myList]; // make a separate copy of the array
        var index = array.indexOf(e.target.value)
        if (index !== -1) {
          array.splice(index, 1);
          this.setState({myList: array});
        }
      }



    render() {
      
        return (
<div>
    <h1> my list</h1>
{/* <ul>
            {this.state.myList.map(function(name, index){
                return <li key={ index }>{name}    </li>;
              })}
        </ul> */}



        <ul class="list-group">
 
  {this.state.myList.map(function(name, index){
                return <li class="list-group-item" key={ index }>{name}      <button type="button" class="btn btn-danger" onClick={() => this.handelOnClick()} >Delet</button>
                </li>;  
              })}
</ul>

        <div class="form-group">
      <label for="usr">Task:</label>
      {/* <input type="text" class="form-control" id="" onChange={(e) =>this.state.myList.concat(e.target.value)}  /> */}
      <input type="text" class="form-control" id="" onChange={(e) =>this.setState({ newvalue: e.target.value })}  />

      <button type="button" class="btn btn-primary btn-lg btn-block" onClick={() => this.handelOnClick()} >submit</button>

    </div>
       

</div>
        
        );
    }
}

export default ToDoList;
